mapper.update:
update api_request
project_id = #{projectId}, api_name = #{apiName}, api_desc = #{apiDesc}, method = #{method}, url = #{url}, headers_json = #{headersJson}, body = #{body}, script = #{script}, is_available = #{isAvailable}, is_deleted = #{isDeleted}, create_username = #{createUsername}, create_time = #{createTime}, update_time = #{updateTime},
where id = #{id}
