insert into strategy_case_relation
(
    execut_strategy_id, case_id, is_deleted, create_time, 
    update_time
)
values

(
    #{obj.executStrategyId}, #{obj.caseId}, #{obj.isDeleted}, #{obj.createTime}, 
    #{obj.updateTime}
)

mapper.update:
update strategy_case_relation
execut_strategy_id = #{executStrategyId}, case_id = #{caseId}, is_deleted = #{isDeleted}, create_time = #{createTime}, update_time = #{updateTime},
where id = #{id}

